mapper.select:
SELECT * FROM (SELECT row_number() OVER (ORDER BY st.st_id DESC) AS rnum, st.st_id AS stId, st.department_id AS deptId, d.department_name AS deptName, st.rrn, st.password, st.st_name AS name, st.gender, st.address, st.email, st.profile, st.national, NVL(r.register_change_id, '재학') AS status FROM st JOIN department d ON st.department_id = d.department_id LEFT OUTER JOIN register r ON st.st_id = r.st_id WHERE 1=1
AND st.st_id LIKE '%'||#{stId}||'%'
AND st.st_name LIKE '%'||#{name}||'%'
AND d.department_name LIKE '%'||#{deptName}||'%'
) A WHERE rnum BETWEEN #{start} AND #{end}
AND status = #{status}
